import React from "react";
// TODO: import useFormik from formik library
import {useFormik} from 'formik';

function App() {
  // TODO: add a const called formik assigned to useFormik()
  const formik = useFormik({
    initialValues: {
      email: '',
      password: ''      
    },
    onSubmit: values =>{
      if (!formik.errors.email && !formik.errors.password) {
        alert("Login Successful");
      }
      console.log('form:', values);
    },

    validate: values =>{
      let errors = {};
      // built-in browser validation : https://html5-tutorial.net/form-validation/validating-email/
      const emailInput = document.querySelector('input[name="email"]');
      if(!values.email) {
        errors.email = 'Field required';
      }
      else if (emailInput && !emailInput.validity.valid) {
        errors.email = 'Username should be an email';
      }

      if (!values.password) {
        errors.password = 'Field required';
      }

      return errors;
    }
  });

  return (
    <div>
      <form onSubmit={formik.handleSubmit}>
        <div>Email:</div>
        <input 
        type="email" 
        name="email" 
        onChange={formik.handleChange} 
        value={formik.values.email}
        // pattern validation : https://html5-tutorial.net/form-validation/validating-email/
        pattern="^[a-zA-Z0-9.!#$%&'*+/=?^_`{|}~-]+@[a-zA-Z0-9-]+(?:\.[a-zA-Z0-9-]+)*$"
        required
        />
        {formik.errors.email ? <div style={{color:'red'}}>{formik.errors.email}</div> : null}        
        <div>Password:</div>
        <input type="text" name="password" onChange={formik.handleChange} value={formik.values.password}/><br/>
        {formik.errors.password ? <div style={{color:'red'}}>{formik.errors.password}</div> : null}                
        <button type="submit">Submit</button>
      </form>      
    </div>
  );
}

export default App;
